public class Maca2 {
    public static void main(String args[]) {
        String m = "";
        int soma = 0;

        for (int i = 0; i < m.length(); i++) {
            char index = m.charAt(i);
            int numero = index - '0';
            soma += (numero % 2);
            System.out.println(soma);
        }
    }
}